"use client";

import React, { forwardRef, useRef } from "react";

import { cn } from "@/lib/utils";
import { AnimatedBeam } from "@/components/ui/animated-beam";
import { IconCode, IconUser } from "@tabler/icons-react";

const Circle = forwardRef<
    HTMLDivElement,
    { className?: string; children?: React.ReactNode }
>(({ className, children }, ref) => {
    return (
        <div
            ref={ref}
            className={cn(
                "z-10 flex size-12 items-center justify-center rounded-full border-2 bg-white p-3 shadow-[0_0_20px_-12px_rgba(0,0,0,0.8)]",
                className,
            )}
        >
            {children}
        </div>
    );
});

Circle.displayName = "Circle";


export function AnimatedBeamMultiple({
    className,
}: {
    className?: string;
}) {
    const containerRef = useRef<HTMLDivElement>(null);
    const div1Ref = useRef<HTMLDivElement>(null);
    const div1aRef = useRef<HTMLDivElement>(null);
    const div2Ref = useRef<HTMLDivElement>(null);
    const div3Ref = useRef<HTMLDivElement>(null);
    const div4Ref = useRef<HTMLDivElement>(null);
    const div5Ref = useRef<HTMLDivElement>(null);
    const div5_5Ref = useRef<HTMLDivElement>(null);
    const div6Ref = useRef<HTMLDivElement>(null);
    const div7Ref = useRef<HTMLDivElement>(null);

    const iconsList = [{
        icon: icons.python,
        ref: div1Ref,
    },
    {
        icon: icons.typescript,
        ref: div1aRef,
    },
    {
        icon: icons.js,
        ref: div2Ref,
    }, {
        icon: icons.cpp,
        ref: div3Ref,
    }, {
        icon: icons.c,
        ref: div4Ref,
    }, {
        icon: icons.node,
        ref: div5Ref,
    }, {
        icon: icons.html,
        ref: div5_5Ref,
    }]


    return (
        <div
            className={cn(
                "relative flex h-[400px] w-full items-center justify-center overflow-hidden rounded-lg bg-background p-10 md:shadow-xl",
                className,
            )}
            ref={containerRef}
        >
            <div className="flex size-full max-w-lg flex-row items-stretch justify-between gap-10">
                <div className="flex flex-col justify-center">
                    <Circle ref={div7Ref} className="p-2">
                        <IconUser color="black" className="size-full" />
                    </Circle>
                </div>
                <div className="flex flex-col justify-center">
                    <Circle ref={div6Ref} className="size-16 p-2">
                        <IconCode color="black" className="size-full" />
                    </Circle>
                </div>
                <div className="flex flex-col justify-center gap-2">
                    {iconsList.map((ele, index) => {
                        return (
                            <Circle key={index} ref={ele.ref} className="p-2">
                                <ele.icon />
                            </Circle>
                        );
                    })}
                </div>
            </div>

            {/* AnimatedBeams */}
            <AnimatedBeam
                containerRef={containerRef}
                fromRef={div1Ref}
                toRef={div6Ref}
                duration={5}
            />
            <AnimatedBeam
                containerRef={containerRef}
                fromRef={div1aRef}
                toRef={div6Ref}
                duration={5}
            />
            <AnimatedBeam
                containerRef={containerRef}
                fromRef={div2Ref}
                toRef={div6Ref}
                duration={5}
            />
            <AnimatedBeam
                containerRef={containerRef}
                fromRef={div3Ref}
                toRef={div6Ref}
                duration={5}
            />
            <AnimatedBeam
                containerRef={containerRef}
                fromRef={div4Ref}
                toRef={div6Ref}
                duration={5}
            />
            <AnimatedBeam
                containerRef={containerRef}
                fromRef={div5Ref}
                toRef={div6Ref}
                duration={5}
            />
            <AnimatedBeam
                containerRef={containerRef}
                fromRef={div5_5Ref}
                toRef={div6Ref}
                duration={5}
            />
            <AnimatedBeam
                containerRef={containerRef}
                fromRef={div6Ref}
                toRef={div7Ref}
                duration={5}
            />
        </div>
    );
}

